var fs = require('fs');

var proc = function(d) {
    var j =     JSON.parse(d);
    var n = [];

    for (var i in j) {
	n.push({k:i,v:j[i]});
    }
    console.log(JSON.stringify(n));
};

fs.readFile("chart.json.old","utf8",function (err,data) {
	if (err) {
	    return console.log(err);
	}
	proc(data);
    });